        else if (metin.toString().equals("Mülk Okunus"))
        {
            icerik = "Bismillahirrahmanirrahim\n" +
                    "1.Tebarekelleziy biyedihilmulku ve huve 'ala kulli şey'in kadiyrun.\n" +
