        else if (metin.toString().equals("Nebe Okunus"))
        {
            icerik = "Bismillahirrahmanirrahim\n" +
                    "1.Amme yetesaelune.\n" +
                    "2.Aninnebeil'azıymi.\n" +
                    "3.Elleziy hum fiyhi muhtelifune.\n" +
                    "4.Kella seya'lemune.\n" +
                    "5.Sümme kella seya'lemune.\n" +
                    "6.Elem nec'alil'arda mihaden.\n" +
                    "7.Velcibale evtaden.\n" +
