        else if (metin.toString().equals("Meryem Okunus"))
        {
            icerik = "Bismillahirrahmanirrahim\n" +
                    "1.Kef ha ya ayn sad\n" +
                    "2.Zikru rahmeti rabbike abdehu zekeriyya\n" +
                    "3.İz nada rabbehu nidaen hafiyya\n" +
                    "4.Kale rabbi innı vehenel azmü minnı veştealer ra'sü şeybev ve lem eküm bi düaike rabbi şekıyya\n" +
